// Otherwise (If Statements with Else)public class ClubBouncer {	public static void main( String[] args ) {		int age = 22;		boolean onGuestList = false;		double allure = 7.5;		String gender = "F";		/* 		if ( !(onGuestList || age >= 21 || (gender.equals("F") && allure >= 8)) )		if ( ! onGuestList && age < 21 && (! gender.equals("F") || allure < 8) )		= OPPOSITE of below 		but unnecessary!! -> ELSE STATMENTS :) */				if ( onGuestList || age >= 21 || (gender.equals("F") && allure >= 8) ) {			System.out.println( "You are allowed to enter the club." );		}				// THIS DOESN'T PRINT!!!		System.out.println( "C-C-C-COMBO BREAKER" );				else {			System.out.println( "You are not allowed to enter the club.");		}	}}// Condition of 'if' statement true? If so, skip 'else'. Otherwise, execute 'else'.// ELSE convenient! Don't have to figure out logical opposite complex Boolean expression.