// More Printingpublic class GasolineReceipt {	public static void main( String[] args ) {		System.out.println( "+------------------------+" );		System.out.println( "|                        |" );		System.out.println( "|      CORNER STORE      |" );		System.out.println( "|                        |" );		System.out.println( "| 2022-01-21  11:23PM    |" );		System.out.println( "|                        |" );		System.out.println( "| Litre:         10.870  |" );		System.out.println( "| Price/litre: £ 2.089   |" );		System.out.println( "|                        |" );		System.out.println( "| Fuel total:  £ 22.71   |" );		System.out.println( "|                        |" );		System.out.println( "+------------------------+" );	}	}